import React, { useEffect, useState } from "react";
import { FaWhatsapp } from "react-icons/fa";
import { FiClock, FiInfo } from "react-icons/fi";
import { Map, Marker, TileLayer } from "react-leaflet";
import {useParams} from 'react-router-dom';

import Sidebar from "../components/sidebar";
import mapIcon from "../utils/mapIcon";

import '../styles/pages/orphanage.css';
import api from "../services/api";

import {
  FacebookShareButton,
  FacebookIcon,
  WhatsappIcon,
    WhatsappShareButton,

} from "react-share";

interface Orphanage {
  latitude: number,
  longitude: number,
  name: string,
  about: string,
  instructions: string,
  opening_hours: string,
  open_on_weekends: string,
  images: Array<{
    id: number,
    url: string
  }>
}

interface OrphanageParams {
  id: string
}

export default function Orphanage() {

  const params = useParams<OrphanageParams>() as OrphanageParams;

  const [orphanage, setOrphanage] = useState<Orphanage>();
  const [activeImageindex, setActiveIndexImage] = useState(0);

  function handleLocationUrl() {
    const url = window.location.href;
    console.log(url)
    return url
  }

  useEffect(() => {
    api.get(`orphanage/${params.id}`).then(response => {
      setOrphanage( response.data);
    })
  }, [params.id]);

  if (!orphanage) {
    return <p>Carregando...</p>
  }

  return (
    <div id="page-orphanage">
      <Sidebar />

      <main>
        <div className="orphanage-details">
          <img src={orphanage.images[activeImageindex].url} alt={orphanage.name} />

          <div className="images">
            {orphanage.images.map((image, index) => {
              return (
              <button 
                key={image.id} 
                className={activeImageindex === index ? 'active' : ''} //Se o índice da imagem é igual ao índice ativo
                type="button"
                onClick={() => {
                  setActiveIndexImage(index);
                }}
              >
               <img src={image.url} alt={orphanage.name} />
              </button>
              )
            })}


          </div>

          <div className="orphanage-details-content">
            <h1>{orphanage.name}</h1>
            <p>{orphanage.about}</p>

            <div className="map-container">
              <Map 
                center={[orphanage.latitude, orphanage.longitude]} 
                zoom={16} 
                style={{ width: '100%', height: 280 }}
                dragging={false}
                touchZoom={false}
                zoomControl={false}
                scrollWheelZoom={false}
                doubleClickZoom={false}
              >
                <TileLayer 
                  url={`https://api.mapbox.com/styles/v1/mapbox/light-v10/tiles/256/{z}/{x}/{y}@2x?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}
                />
                <Marker interactive={false} icon={mapIcon} position={[orphanage.latitude, orphanage.longitude]} />
              </Map>

              <footer>
                <a target="_blank" rel="noopener noreferrer" href={`https://www.google.com/maps/dir/?api=1&destination=${orphanage.latitude},${orphanage.longitude}`}>Ver rotas no Google Maps</a>
              </footer>
            </div>

            <hr />

            <h2>Instruções para visita</h2>
            <p>{orphanage.instructions}</p>

            <div className="open-details">
              <div className="hour">
                <FiClock size={32} color="#15B6D6" />
                Segunda à Sexta <br />
                {orphanage.opening_hours}
              </div>
              { orphanage.open_on_weekends ? (
                <div className="open-on-weekends">
                <FiInfo size={32} color="#39CC83" />
                Atendemos <br />
                fim de semana
              </div>
              ) : (
                <div className="open-on-weekends dont-open">
                <FiInfo size={32} color="#FF669F" />
                Não atendemos <br />
                fim de semana
              </div>
              ) }
            </div>

            <FacebookShareButton url={handleLocationUrl()} quote='Espalhe felicidade e visite' hashtag={'happy'} >
              <FacebookIcon size={64} round={false}/>
            </FacebookShareButton>
            <WhatsappShareButton url={handleLocationUrl()} title='Espalhe felicidade e visite' hashtag={'happy'} >
              <WhatsappIcon size={64} round={false}/>
            </WhatsappShareButton>
            WhatsappShareButton
            {/* <button type="button" className="contact-button">
            <FacebookShareCount url={shareUrl} />
              <FaWhatsapp size={20} color="#FFF" />
              Entrar em contato
            </button> */}
          </div>
        </div>
      </main>
    </div>
  );
}
